INSERT INTO `data_book` (`id`, `book_name`, `publisher`, `book_type`, `book_category`, `author`, `description`, `store`, `image_url`, `price`, `type`, `promotion`) VALUES
(10002, 'หนังสือ 500 เคล็ดลับการออกแบบ ร้านอาหารและเครื่องดื่มขนาดเล็ก', 'My House Publication', 'งานอดิเรก / งานฝีมือ', 'ออกแบบและตกแต่งบ้าน', 'กองบรรณาธิการบ้านและสวน', '    หนังสือเล่มนี้ได้รวบรวมแบบร้านขนาดเล็กสวย ๆ อาทิเช่น ร้านขนม ร้าน Caf\'e ร้านซื้อกลับบ้าน ร้านเบเกอรี่ ร้านอาหารทานเล่น ร้านอาหารขนาดเล็ก เราได้เลือกแบบร้านอาหารมามากกว่า 500 แบบเพื่อสอนให้คุณได้รู้จักวิธีที่จะทำให้ร้านของคุณได้รับความนิยม จากด้านนอกร้าน เคาน์เตอร์บาร์ โซนที่นั่ง และโซนซื้อกลับบ้าน เราจะบอกให้คุณรู้วิธีการจัดองค์ประกอบต่าง ๆ ให้กับพื้นที่ วิธีการใช้วัสดุ วิธีการสร้างบรรยากาศให้กับพื้นที่ และเทคนิคต่าง ๆ ที่สามารถนำมาใช้ด้วยกันได้', 0, 'https://pim-cdn0.ofm.co.th/products/large/D093761.jpg', 700, 'สารคดี', 0),
(10003, 'หนังสือ ไม้ใบ FOLIAGE PLANTS', 'บ้านและสวน, สนพ.', 'งานอดิเรก / งานฝีมือ', 'จัดและออกแบบสวน', 'ภวพล ศุภนันทนานนท์', 'รวมเรื่องราวของ \"ไม้ใบ\" ตั้งแต่ประวัติความเป็นมา การจำแนก ดูแลรักษา ขยายพันธุ์ ประโยชน์ของไม้ใบในด้านไม้ประดับ ทั้งภายในบ้านและนอกบ้าน พร้อมรวบรวมพันธุ์ไม้ใบที่นิยมกันในตลาดต้นไม้ สอดแทรกเกร็ดความรู้ที่มาและเทคนิคการปลูกเลี้ยง รวมมากกว่า 500 ชนิด/พันธุ์ เนื้อหาในเล่มจะช่วยเปิดโลกของคุณให้กว้างขึ้นกว่าที่เคย และเป็นประโยชน์กับผู้ที่ชื่นชอบ \"ไม้ใบ\" อย่างแน่นอน!', 5, 'https://pim-cdn0.ofm.co.th/products/large/D094071.jpg', 695, 'สารคดี', 0),
(10004, 'หนังสือ กล้วยไม้ Orchids สนพ. บ้านและสวน', 'บ้านและสวน, สนพ.', 'งานอดิเรก / งานฝีมือ', 'พืชและธรรมชาติ', 'ภวพล ศุภนันทนานนท์', 'หนังสือ กล้วยไม้ เล่มนี้ เป็นอีกเล่มหนึ่งในชุด Plant Library ผู้เขียนมีความตั้งใจเป็นอย่างยิ่งที่จะอัพเดทชื่อ ข้อมูล และสถานะของกล้วยไม้ชนิดแท้หลายชนิดให้ทันสมัยตามที่ควรจะเป็น นอกจากนี้ยังอยากนำเสนอกล้วยไม้ลูกผสมมากมายหลายสายพันธุ์ที่น่าสนใจให้เป็นที่รู้จักกันในวงกว้างมากขึ้นกว่าเดิม รวมมากกว่า 500 ชนิดและพันธุ์ ซึ่งแต่ละต้นระบุชื่อ ลักษณะเด่น และเกร็ดความรู้ต่างๆ ที่ยังไม่เคยมีการกล่าวถึงมาก่อน', 100, 'https://pim-cdn0.ofm.co.th/products/large/DA06562.jpg', 495, 'สารคดี', 0),
(10005, 'หนังสือ ทฤษฎีดนตรี 101 สนพ.แอร์โรว์ มัลติมีเดีย', 'แอร์โรว์ มัลติมีเดีย', 'ความรู้รอบตัว', 'หนังสือไลฟ์สไตล์', 'ไบรอัน บูน, มาร์ค เชินบรุน', 'เรียนรู้พื้นฐานของทฤษฎีดนตรีในคำแนะนำที่ครอบคลุมและเข้าใจง่ายนี้ตั้งแต่คลาสสิกไปจนถึงฮาร์ดร็อค และแจ๊สไปจนถึงฮิปฮอป ดนตรีมีการพัฒนาอย่างต่อเนื่อง แต่พื้นฐานหลายอย่างยังคงเหมือนเดิม การเข้าใจพื้นฐานเหล่านี้เป็นกุญแจสำคัญในการเป็นนักดนตรีที่ประสบความสำเร็จและเป็นคนรักดนตรีที่รอบรู้ทฤษฎีดนตรี 101 ครอบคลุมทุกสิ่งที่นักดนตรีมือใหม่และผู้เรียนรู้ตลอดชีวิตจำเป็นต้องรู้', 99, 'https://pim-cdn0.ofm.co.th/products/large/DA05404.jpg', 450, 'สารคดี', 0),
(10007, 'หนังสือ นามานุกรมพุทธประวัติ ฉบับประชาชน', 'สถาพรบุ๊คส์', 'โหราศาสตร์ / ความเชื่อ', 'ศาสนา', 'ส. พลายน้อย', 'ศาสนาพุทธเป็นศาสนาประจำชาติไทย แต่จะมีสักกี่คนที่ทราบถึงประวัติของพระสัมมาสัมพุทธเจ้า ผู้ทรงเป็นศาสดาของพระพุทธศาสนา หนังสือเรื่องนี้จึงได้รวบรวมคำศัพท์เกี่ยวกับประวัติ และเรื่องราวต่างๆ ของพระพุทธเจ้า', 100, 'https://pim-cdn0.ofm.co.th/products/large/D098358.jpg', 300, 'สารคดี', 0),
(10008, 'หนังสือ เรื่องของโระ', 'บ้านพระอาทิตย์', 'งานอดิเรก / งานฝีมือ', 'สัตว์เลี้ยง', 'ธรณ์ ธำรงนาวาสวัสดิ์', 'บันทึกของหมาสาวขี้อ้อน กับเจ้านายใจอ่อน ผู้ตกเป็นทาสเธอ  หนังสือเล่มแรกในรอบ 5 ปี ของธรณ์ ธำรงนาวาสวัสดิ์ จากนักเล่าเรื่องปลาและสรรพชีวิตใต้ท้องทะเล เปลี่ยนเป็นเล่าเรื่องหมา และจะทำให้ผู้อ่านหลงรักหมาพอกับหลงรักปลาและสรรพชีวิตในท้องทะเลไทย ภายในเล่มพบกับ เรื่องเล่าของ ชิโระ สุนัขสาวสายพันธุ์อเมริกันบูลลี่ อายุ 2 ปี ที่ทั้งอ้วน ทั้งอ้อน และเป็นตัวป่วนประจำบ้าน ที่นำความครื้นเครงทั้งคนและหมา อาจารย์ธรณ์ จะพาคุณไปปาร์ตี้กับหมา ที่จะทำให้ทั้งคนรักหมายิ่งหลงรัก และคนไม่รักหมาอาจต้องเปลี่ยนใจ เหมาะสำหรับ คนรักหมา ผู้ที่เลี้ยงหมา และผู้ที่สนใจจะเลี้ยงหมาทุกท่าน รวมถึงทาสแมวทุกคน', 100, 'https://pim-cdn0.ofm.co.th/products/large/D092584.jpg', 180, 'สารคดี', 0),
